/**
 * Created by agros on 08.06.2019.
 */
import React, {useEffect} from 'react';
import {connect} from 'react-redux';
import {fetchContactAction} from '../../actions/contact';

import EditForm from './EditForm';

const EditContact = ({contact:{contact, loading}, fetchContactAction, match}) => {
    useEffect( ()=> {
        fetchContactAction(match.params.id);
    }, [fetchContactAction]);

    return (
        loading ? <div>Loading...</div> : <EditForm contact={contact} updateMode={true}/>
    );
};

const mapStateToProps = state => (
    {contact: state.contact}
);

export default connect(mapStateToProps, {fetchContactAction})(EditContact);
