/**
 * Created by agros on 07.06.2019.
 */
import React, {useEffect, useState, Fragment} from 'react';
import {graphql} from 'react-apollo';
import {Link} from 'react-router-dom';

import  ContactItem from '../contacts/ContactItem';
import fetchContact from '../../queries/fetchContact';

const CallsList = ({data:{contact, loading}}) => {
    console.log(contact);
    return (
        <Fragment>
            <Link className="btn btn-light my-1" to='/'>Go Back</Link>
            {loading? <div>Loading...</div> : (
                contact.history.calls.map(call => {
                    return (
                        <Fragment key={call.id}>
                            <h3>{call.contact.name}</h3>
                            <p>{call.time}</p>
                        </Fragment>
                    )
                })
            )}
        </Fragment>

    );
};


export default graphql(fetchContact, {
    options: ( props => {return {variables: {id: props.match.params.id}}})
})(CallsList);
