/**
 * Created by agros on 08.06.2019.
 */
import React from 'react';
import {graphql} from 'react-apollo';

import EditForm from './EditFormForm';
import fetchContact from '../../queries/fetchContact';

const EditContact = (props) => {
    return (
        props.data.loading ? <div>Loading...</div> : <EditForm contact={props.data.contact} updateMode={true}/>
    );
};

export default graphql(fetchContact, {
    options: ( props => {return {variables: {id: props.match.params.id}}})
})(EditContact);
