/**
 * Created by agros on 08.06.2019.
 */
import React, {useEffect, Fragment, useState} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import {fetchContactAction, editContactAction, clearContact} from '../../actions/contact';

const EditContact = ({contact:{contact, loading}, fetchContactAction, editContactAction, clearContact, match}) => {
    const [formData, setFormData] = useState({
        id: '',
        name: '',
        phone: '',
        email: ''
    });

    useEffect( ()=> {
        fetchContactAction(match.params.id);
        setFormData({
            id: contact.id,
            name: contact.name,
            phone: contact.phone,
            email: contact.email
        });
        return ()=> clearContact();
    }, []);

    const onChangeHandler = e => {
        setFormData({...formData, [e.target.name]: e.target.value});
    };

    //Method for post adding or updating
    const onFormSubmit = e => {
        e.preventDefault();
        editContactAction(formData);
    };

    return (
        loading || contact === null ? <div>Loading...</div> : (
            <Fragment>
                <Link className="btn btn-light my-1" to='/'>Go Back</Link>
                <div className="post-form">
                    <div className="bg-primary p">
                        <h3>Create a contact</h3>
                    </div>
                    <form className="form my-1" onSubmit={ (e)=> onFormSubmit(e)}>
                        <div className="form-group">
                            <input type="text" name="name" value={formData.name} placeholder="Input a name"  onChange={(e) => onChangeHandler(e)} required/>
                        </div>
                        <div className="form-group">
                            <input type="text" name="phone" value={formData.phone} placeholder="Input a phone number"  onChange={(e) => onChangeHandler(e)} required/>
                        </div>
                        <span>Format: country code - operator code - 123 - 4567</span>
                        <div className="form-group">
                            <input type="email" name="email" value={formData.email} placeholder="Input an email"  onChange={(e) => onChangeHandler(e)} required/>
                        </div>
                        <input type="submit" className="btn btn-dark my-1" value="Update" />
                    </form>
                </div>
            </Fragment>
        )
    )
};

const mapStateToProps = state => (
    {contact: state.contact}
);

export default connect(mapStateToProps, {fetchContactAction, editContactAction, clearContact})(EditContact);
