/**
 * Created by agros on 08.06.2019.
 */
import React from 'react';
import {connect} from 'react-redux';
import {fetchContactAction} from '../../actions/contact';

import EditForm from './EditForm';

const EditContact = ({contact}) => {
    return (
        props.data.loading ? <div>Loading...</div> : <EditForm contact={props.data.contact} updateMode={true}/>
    );
};

const mapStatetoProps = state => (
    {
        contact: state.contact
    }
);

export default connect(mapStatetoProps, {fetchContactAction})(EditContact);
