/**
 * Created by agros on 08.06.2019.
 */
import { ApolloConsumer  } from 'react-apollo';
import {GET_CONTACT, GET_CONTACTS, CREATE_CONTACT, DELETE_CONTACT, EDIT_CONTACT} from './actionTypes';
import React from 'react';
import {client} from '../utils/apolloClient';

import fetchContact from '../queries/fetchContact';
import fetchContacts from '../queries/fetchContacts';
import createContact from '../queries/createContact';
import deleteContact from '../queries/deleteContact';
import editContact from '../queries/editContact';

export const fetchContactAction = id => async dispatch => {
    const request = await client.query({
        query: fetchContact,
        variables: {
            id: id
        }
    });
    const response = await request;
    dispatch({
        type: GET_CONTACT,
        payload: response.data
    })
};

export const fetchContactsAction = () => async dispatch => {
    const request = await client.query({
        query: fetchContacts
    });
    const response = await request;
    dispatch({
        type: GET_CONTACTS,
        payload: response.data
    })
};

export const createContactAction = formData => async dispatch => {
    const request = await client.mutate({
        mutation: createContact,
        variables: {
            name: formData.name,
            phone: formData.phone,
            email: formData.email
        }
    });

    const response = await request;
    dispatch({
        type: CREATE_CONTACT,
        payload: response.data.addContact
    })
};

export const editContactAction = formData => async dispatch => {
    const request = await client.mutate({
        mutation: editContact,
        variables: {
            name: formData.name,
            phone: formData.phone,
            email: formData.email
        }
    });

    const response = await request;
    dispatch({
        type: CREATE_CONTACT,
        payload: response.data.addContact
    })
};

export const deleteContactAction = id => async dispatch => {
    const request = await client.mutate({
        mutation: deleteContact,
        variables: {
            id: id
        }
    });
    await request;
    dispatch({
        type: DELETE_CONTACT,
        payload: id
    })
};
