/**
 * Created by agros on 07.06.2019.
 */
import React, {useEffect, useState, Fragment} from 'react';
import PropTypes from 'prop-types';
import gqt from 'graphql-tag';
import {graphql} from 'react-apollo';
import {Link} from 'react-router-dom';

import  ContactItem from '../contacts/ContactItem';

const CallsList = ({contact}, mutation) => {

    //Loading posts bi id
    useEffect(() => {
        mutation({
            variables: {id: contact.id}
        })
    },);

    return (
        <Fragmrnt>
            <Link className="btn btn-light my-1" to='/'>Go Back</Link>
            <ContactItem contact={contact} />
        </Fragmrnt>

    );
};

const callsQuery = gql`
query ($id: String!) {
  contact(id: $id) {
    history{
      calls{
        id
        contact {
          name
        }
        time
      }
    }
  }
}
`;

export default graphql(callsQuery)(CallsList);
