export const fetchContacts = gqt`
    query ContactQuery($first: Int, $skip: Int, $orderBy: LinkOrderByInput) {
    contacts (first: $first, skip: $skip, orderBy: $orderBy) {
    {
        contacts {
            id
            name
            phone
            email
        }
    }
`;
