/**
 * Created by agros on 08.06.2019.
 */
import {
    GET_CONTACT
} from '../actions/actionTypes';

const initialState = {
    contacts: [],
    contact: null,
    loading: true
};

export default function(state = initialState, action) {
    const {type, payload} = action;

    switch(type) {
        case GET_CONTACT:
            return {
                ...state,
                contact: payload,
                loading: false
            };
        default:    
            return state;
    }
};
