/**
 * Created by agros on 07.06.2019.
 */
import React, {Fragment, useEffect} from 'react';
import {graphql} from 'react-apollo';
import {Link} from 'react-router-dom';
import {connect} from 'react-redux';
import {fetchContact} from '../../actions/contact';

import fetchQuery from '../../queries/fetchContact';

const CallsList = ({contact:{contact, loading}, match, fetchContact}) => {

    useEffect( ()=> {
        fetchContact(match.params.id, fetchQuery);
    }, [fetchContact]);

    return (
        <Fragment>
            <Link className="btn btn-light my-1" to='/'>Go Back</Link>
            <div className="bg-primary p">
                <h3>List of calls</h3>
            </div>
            {loading? <div>Loading...</div> : (
                contact.history.calls.map(call => {
                    return (
                        <div className="post bg-white p-1 my-1" key={call.id}>
                            <h4>{call.contact.name}</h4>
                            <p className="my-1">{call.time}</p>
                        </div>
                    )
                })
            )}
        </Fragment>

    );
};

const mapDispatchToProps = state => (
    {contact: state.contact}
);

export default connect(mapDispatchToProps, {fetchContact})(CallsList);
