/**
 * Created by agros on 08.06.2019.
 */
import React, {useState} from 'react';
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {createContactAction} from '../../actions/contact';


const ContactForm = ({createContactAction}) => {
    //Variable for storing data from from and method from its updating
    //Initial value is taken from post props
    const [formData, setFormData] = useState({
        name: '',
        phone: '',
        email: ''
    });

    const onChangeHandler = e => {
        setFormData({...formData, [e.target.name]: e.target.value});
    };

    //Method for post adding or updating
    const onFormSubmit = e => {
        e.preventDefault();
        createContactAction(formData);
        setFormData({
            name: '',
            phone: '',
            email: ''
        });
        
    };

    return (
        <div className="post-form">
            <div className="bg-primary p">
                <h3>Create a contact</h3>
            </div>
            <form className="form my-1" onSubmit={ (e)=> onFormSubmit(e)}>
                <div className="form-group">
                    <input type="text" name="name" value={formData.name} onChange={(e) => onChangeHandler(e)} required/>
                    <label for="input" className="control-label">Your name</label>
                    <i className="bar"></i>
                </div>
                <div className="form-group">
                    <input type="text" name="phone" value={formData.phone} onChange={(e) => onChangeHandler(e)} required/>
                    <label for="input" className="control-label">Your phone</label>
                    <i className="bar"></i>
                </div>
                <span>Format: country code - operator code - 123 - 4567</span>
                <div className="form-group">
                    <input type="email" name="email" value={formData.email} placeholder="Input an email"  onChange={(e) => onChangeHandler(e)} required/>
                </div>
                <input type="submit" className="button" value="Create" />
            </form>
        </div>
    );
};


export default connect(null, {createContactAction})(ContactForm);
