/**
 * Created by agros on 08.06.2019.
 */
import { Query, Mutation } from 'react-apollo';
import {GET_CONTACT} from './actionTypes';
import React from 'react';

import fetchQuery from '../queries/fetchContact';

export const fetchContact = id => dispatch => {

    const contacFetch = id => (
        <Query query={fetchQuery} variables={ id }>
            { props => {
                console.log(props);
                return props;
            }}
        </Query>
    );

    const contactData = contacFetch(id);
    console.log(contactData);

    dispatch({
        type: GET_CONTACT,
        payload: contactData,
        loading: false
    });

};
