/**
 * Created by agros on 08.06.2019.
 */
import React from 'react';
import {graphql} from 'react-apollo';

import ContactForm from './ContactForm';
import fetchContact from '../../queries/fetchContact';

const EditContact = ({data: contact, loading}) => {
    console.log(loading);
    return (
        loading ? <div>Loading...</div> : <ContactForm contact={contact}/>
    );
};

export default graphql(fetchContact, {
    options: ( props => {return {variables: {id: props.match.params.id}}})
})(EditContact);
