/**
 * Created by agros on 08.06.2019.
 */
import React, {useEffect, Fragment, useState} from 'react';
import {connect} from 'react-redux';
import {Link} from 'react-router-dom';
import {fetchContactAction, editContactAction, clearContact} from '../../actions/contact';

const EditContact = ({contact:{contact, loading}, fetchContactAction, editContactAction, clearContact, match, history}) => {
    const [formData, setFormData] = useState({});

    useEffect( ()=> {
        fetchContactAction(match.params.id);
        return ()=> clearContact();
    }, []);

    useEffect( () => {
        setFormData({
            id: contact ? contact.id : '',
            name: contact ? contact.name : '',
            phone: contact ? contact.phone : '',
            email: contact ? contact.email : ''
        })
    }, [contact]);

    const onChangeHandler = e => {
        setFormData({...formData, [e.target.name]: e.target.value});
    };

    //Method for post adding or updating
    const onFormSubmit = e => {
        e.preventDefault();
        editContactAction(formData);
    };
    console.log(history);
    return (
            <Fragment>
                <Link className="text-primary" to='/'><i class="fas fa-angle-double-left"></i> Go Back</Link>
                {loading || contact === null ? <div>Loading...</div> : (
                    <div className="post-form">
                        <form className="form my-1" onSubmit={ (e)=> onFormSubmit(e)}>
                            <div className="form-group">
                                <input type="text" name="name" value={formData.name} placeholder="Input a name"  onChange={(e) => onChangeHandler(e)} required="required"/>
                                <i className="bar"></i>
                            </div>
                            <div className="form-group">
                                <input type="text" name="phone" value={formData.phone} placeholder="Input a phone number"  onChange={(e) => onChangeHandler(e)} required/>
                                <i className="bar"></i>
                            </div>
                            <div className="form-group">
                                <input type="email" name="email" value={formData.email} placeholder="Input an email"  onChange={(e) => onChangeHandler(e)} required/>
                                <i className="bar"></i>
                            </div>
                            <button type="submit" className="button" value="Update"><span>Update</span></button>
                        </form>
                    </div>
                )}
            </Fragment>

    )
};

const mapStateToProps = state => (
    {contact: state.contact}
);

export default connect(mapStateToProps, {fetchContactAction, editContactAction, clearContact})(EditContact);
