/**
 * Created by agros on 08.06.2019.
 */
import React, {Fragment, useState} from 'react';
import PropTypes from 'prop-types';
import {Link, withRouter} from 'react-router-dom';
import gqt from 'graphql-tag';
import {graphql} from 'react-apollo';


//Stateful component for displaying single post inside of a list
const ContactItem  = ({contact:{id, name, phone, email}}) => {
    
    const onDeleteClick = () => {
        mutate({
            variables: {
                id
            }
        });
    }

    return (
        <div className="post bg-white p-1 my-1">
            <h4>{name}</h4>
            <p className="my-1">
                {phone}
            </p>
            <p className="my-1">
                {email}
            </p>
            <Link to={`/contacts/${id}`} className="btn btn-primary">
                List of calls
            </Link>
            <button className="btn btn-primary" >
                Edit Contact
            </button>
            <button className="btn btn-danger" >
                Delete Contact
            </button>
        </div>
    );
};

const deleteContactMutation = gqt`
    mutation DeleteContact (
        $id: String
    ){
        deleteContact(id: $id){
            id
        }
    }
`;

export default ContactItem;
