/**
 * Created by agros on 08.06.2019.
 */
import React, {Fragment, useState} from 'react';
import PropTypes from 'prop-types';
import {Link, withRouter} from 'react-router-dom';
import gqt from 'graphql-tag';
import {graphql} from 'react-apollo';

import fetchContacts from '../../queries/fetchContacts';

const ContactItem  = ({contact:{id, name, phone, email, }, mutate}) => {
    
    const onDeleteClick = () => {
        mutate({
            variables: {id: id},
            refetchQueries: [{ query: fetchContacts }]
        });
    };

    return (
        <div className="post bg-white p-1 my-1">
            <h4>{name}</h4>
            <p className="my-1">
                {phone}
            </p>
            <p className="my-1">
                {email}
            </p>
            <Link to={`/contacts/${id}`} className="btn btn-primary">
                List of calls
            </Link>
            <button className="btn btn-primary" >
                Edit Contact
            </button>
            <button className="btn btn-danger" onClick={onDeleteClick}>
                Delete Contact
            </button>
        </div>
    );
};

const deleteContactMutation = gqt`
    mutation DeleteContact (
        $id: String!
    ){
        deleteContact(id: $id){
            id
        }
    }
`;

export default graphql(deleteContactMutation)(ContactItem);
