/**
 * Created by agros on 08.06.2019.
 */
import React, {useState} from 'react';
import PropTypes from 'prop-types';
import gqt from 'graphql-tag';
import {graphql} from 'react-apollo';


//Stateful component for form displaying.
//Has two modes for post creation and post updating. Depends on value of updateMode.
const ContactForm = ({ contact, updateMode, submitted, mutate}) => {

    //Variable for storing data from from and method from its updating
    //Initial value is taken from post props
    const [formData, setFormData] = useState({
        name: contact.title,
        phone: contact.phone,
        email: contact.email
    });

    const onChangeHandler = e => {
        setFormData({...formData, [e.target.name]: e.target.value});
    };

    //Method for post adding or updating
    const onFormSubmit = e => {
        e.preventDefault();
        if (updateMode) {
            submitted(false);
        } else {
            mutate({
                variables: {
                    name: formData.name,
                    phone: formData.phone,
                    email: formData.email
                },
                refetchQueries: [{  }]
            });

            setFormData({
                name: '',
                phone: '',
                email: ''
            });
        }
    };

    return (
        <div className="post-form">
            <div className="bg-primary p">
                <h3>Create a contact</h3>
            </div>
            <form className="form my-1" onSubmit={ (e)=> onFormSubmit(e)}>
                <div className="form-group">
                    <input type="text" name="name" value={formData.name} placeholder="Input a name" required onChange={(e) => onChangeHandler(e)} required/>
                </div>
                <div className="form-group">
                    <input type="text" name="phone"   value={formData.phone} placeholder="Input a phone number" required onChange={(e) => onChangeHandler(e)} required/>
                </div>
                <span>Format: country code - operator code - 123 - 4567</span>
                <div className="form-group">
                    <input type="email" name="email" value={formData.email} placeholder="Input an email" required onChange={(e) => onChangeHandler(e)} required/>
                </div>
                <input type="submit" className="btn btn-dark my-1" value="Create" />
            </form>
        </div>
    );
};

const addContactMutation = gqt`
    mutation AddContact (
        $name: String!,
        $phone: String!,
        $email: String!
    ){
        addContact(name: $name, phone: $phone, email: $email){
            name
            phone
            email
        }
    }
`;

ContactForm.defaultProps = {
    contact: {
        name: '',
        phone: '',
        email: ''
    },
    updateMode: false
};

ContactForm.propTypes = {
    contact: PropTypes.object
};

export default graphql(addContactMutation)(ContactForm);
