/**
 * Created by agros on 08.06.2019.
 */
import React, {useEffect} from 'react';
import {connect} from 'react-redux';
import {fetchContactAction} from '../../actions/contact';
import {editContactAction} from '../../actions/contact';

import EditForm from './EditForm';

const EditContact = ({contact:{contact, loading}, fetchContactAction, editContactAction, match}) => {
    useEffect( ()=> {
        fetchContactAction(match.params.id);
    }, [fetchContactAction, match.params.id]);

    return (
        loading ? <div>Loading...</div> : <EditForm contact={contact} edit={editContactAction}/>
    );
};

const mapStateToProps = state => (
    {contact: state.contact}
);

export default connect(mapStateToProps, {fetchContactAction, editContactAction})(EditContact);
