/**
 * Created by agros on 08.06.2019.
 */
import { ApolloConsumer  } from 'react-apollo';
import {GET_CONTACT, GET_CONTACTS, DELETE_CONTACT} from './actionTypes';
import React from 'react';
import {client} from '../utils/apolloClient';

import fetchContact from '../queries/fetchContact';
import fetchContacts from '../queries/fetchContacts';
import deleteContact from '../queries/deleteContact';

export const fetchContactAction = id => async dispatch => {
    const request = await client.query({
        query: fetchContact,
        variables: {
            id: id
        }
    });
    const response = await request;
    dispatch({
        type: GET_CONTACT,
        payload: response.data
    })
};

export const fetchContactsAction = () => async dispatch => {
    const request = await client.query({
        query: fetchContacts
    });
    const response = await request;
    dispatch({
        type: GET_CONTACTS,
        payload: response.data
    })
};

export const deleteContactAction = () => async dispatch => {
    const request = await client.query({
        query: deleteContact,
        variables: {
            id: id
        }
    });
    await request;
    dispatch({
        type: DELETE_CONTACT
    })
};
